	@Test(priority=2)
	public static void Logout() throws InterruptedException
	{		
		//Logout
		driver.findElement(By.cssSelector(".fa-power-off")).click();
		driver.close();
	}
	
	public void Renew(String password,ITestContext context) throws InterruptedException
	{
        	
        	//Enter the password
        	driver.findElement(By.xpath("//ngb-modal-window[2]/div/div/app-mod-authenticate/div/div/form/div/input")).clear();
        	driver.findElement(By.xpath("//ngb-modal-window[2]/div/div/app-mod-authenticate/div/div/form/div/input")).sendKeys(password);
	        
        	//Click on OK
        	driver.findElement(By.xpath("//ngb-modal-window[2]/div/div/app-mod-authenticate/div/div/form/div[2]/button[2]")).click();
        	Thread.sleep(1000);
    		
        	 //Go to Work space
    		driver.findElement(By.xpath("//li/div/div")).click();
    		Thread.sleep(1000);
    		
    		//Verification of connections
    		String Status1=driver.findElement(By.xpath("//datatable-body-cell[4]/div/span")).getText();
    		String Status2=driver.findElement(By.xpath("//datatable-row-wrapper[2]/datatable-body-row/div[2]/datatable-body-cell[4]/div/span")).getText();
    		
    		if(Status1.equalsIgnoreCase("Connected") && Status2.equalsIgnoreCase("Connected"))
    		{
    			context.setAttribute("Status", 1);
    			context.setAttribute("Comment", "WGS are connected");
    			
    			System.out.println("WGS are connected");
    		}
    		else
    		{
    			context.setAttribute("Status", 5);
    			context.setAttribute("Comment", "WGS are not connected");
    			System.out.println("WGS are not connected");
    		}
	}

